import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { RegisterRequest } from '../model/RegisterRequest';

@Injectable({
  providedIn: 'root'
})
export class UserService {

  constructor(private http:HttpClient) { }

  async registerCustomer(request: RegisterRequest): Promise<number> {
    try {
      const response = await this.http.post('http://localhost:9898/api/customer/register', request).toPromise();
      console.log(response);
      return response.status;
    } catch (error) {
      console.log(error);
      return error.status;
    }
  }  
}
