import { AbstractControl, FormGroup } from '@angular/forms';
import { filter, take } from 'rxjs/operators';

export function passwordMatchValidator(control: AbstractControl) {
  const password = control.get('password');
  const confirmPassword = control.get('confirmPassword');

  if (!password || !confirmPassword) {
    // if the form controls are not yet initialized, return null
    return null;
  }

  password.valueChanges
    .pipe(
      filter(() => confirmPassword.value !== ''),
      take(1)
    )
    .subscribe(() => confirmPassword.updateValueAndValidity());

  confirmPassword.valueChanges
    .pipe(
      filter(() => password.value !== ''),
      take(1)
    )
    .subscribe(() => password.updateValueAndValidity());

  console.log("Password: ", password);
  console.log("Password: ", confirmPassword);
  if (password.value !== confirmPassword.value) {
    return { passwordMismatch: true };
  }

  return null;
}
