import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { RegisterRequest } from '../model/RegisterRequest';
import { UserService } from '../service/user.service';

@Component({
  selector: 'app-customer-registration',
  templateUrl: './customer-registration.component.html',
  styleUrls: ['./customer-registration.component.css']
})
export class CustomerRegistrationComponent {
  registerForm: any;
  request: RegisterRequest = new RegisterRequest();

  constructor(private _formBuilder:FormBuilder, private http: HttpClient, private _userService: UserService){};

  ngOnInit(): void{
    this.registerForm = this._formBuilder.group({
      username: ['', [Validators.required, Validators.minLength(3), Validators.maxLength(30)]],
      password: ['', [Validators.required, Validators.minLength(8), Validators.maxLength(50)]],
      secretQuestion: ['', Validators.required],
      secretAnswer: ['', Validators.required]
    })
  }

  addCustomer():void{
    if(this.registerForm.valid){
      this._userService.registerCustomer(this.request);
    }
    return;
  }

  get f(){
    return this.registerForm.controls;
  }
}
<div class="container main-content mt-5">
    <div class="row d-flex justify-content-center align-items-center">
      <div class="col-md-6 text-center">
        <img src="/assets/images/bank-logo.png" class="img-fluid" alt="Bank Logo">
      </div>
      <div class="col-md-5">
        <form action = "/api/customer/register" method = "POST">
          <div class="form-group">
            <label for="username">Username</label>
            <input type="text" class="form-control" id="username" name="username" required>
          </div>
          <div class="form-group">
            <label for="password">Password</label>
            <input type="password" class="form-control" id="password" name="password" required>
          </div>
          <div class="form-group">
            <label for="confirm-password">Confirm Password</label>
            <input type="password" class="form-control" id="confirm-password" name="confirm-password" required>
          </div>
          <div class="form-group">
            <label for="secretQuestion">Security Question</label>
            <select class="form-control" id="security-question" name="security-question" required>
              <option value="">-- Select a security question --</option>
              <option value="1">What is your mother's maiden name?</option>
              <option value="2">What is your favorite book?</option>
              <option value="3">What is the name of your first pet?</option>
              <option value="4">What is the name of the city where you were born?</option>
              <option value="5">What is your favorite movie?</option>
            </select>
          </div>
          <div class="form-group">
            <label for="secretAnswer">Answer</label>
            <input type="text" class="form-control" id="security-answer" name="security-answer" required>
          </div>
          <div class="form-group">
            <button type="submit" class="btn btn-primary">Register</button>
          </div>
        </form>
      </div>
    </div>
  </div>
